// frontend/src/PropertyComparison.js
import React, { useState } from 'react';
import { propertyData, getUniqueLocations } from './data'; 

// Pre-calculate unique locations once
const availableLocations = getUniqueLocations(propertyData);

// ------------------------------------------
// Helper Component: The Property Card (Simplified Listing)
// ------------------------------------------
const PropertyCard = ({ property, label }) => {
    if (!property) {
        return (
            <div className="property-card empty-card">
                <h3 className="card-label">{label}</h3>
                <p>Select a location from the dropdowns above to see a comparison.</p>
            </div>
        );
    }
    
    // Format price
    const formattedPrice = property.price.toLocaleString('en-US', {
        style: 'currency',
        currency: 'USD',
        maximumFractionDigits: 0,
    });

    // Combine Bedrooms, Bathrooms, and Size into a single line of text 
    const sizeString = String(property.size_sqft);
    const sizeDisplay = sizeString.includes('/') ? 
        sizeString + ' sq ft' : 
        `${sizeString} sq ft`;

    const keyDataLine = `${property.bedrooms} Bed, ${property.bathrooms} Bath, ${sizeDisplay}`;

    // Note: If property.image_url is a full URL, you can display it here. 
    // Assuming a placeholder URL is used for now.
    const imageLink = property.image_url.startsWith('http') ? property.image_url : 'path/to/image.jpg';


    return (
        <div className="property-card">
            {/* Card Label/Title above the image */}
            <h3 className="card-label">{property.title}</h3> 
            
            <img 
                src={property.image_url} 
                alt={property.title} 
                className="property-image" 
            />
            
           

            <div className="card-details">
                {/* Price */}
                <p className="price-tag">
                    Price: {formattedPrice} 
                    {/* The "EST" tag is removed for this simpler, cleaner look */}
                </p>
                
                {/* Location */}
                <p className="location-title">
                    <span style={{ fontWeight: 'bold' }}>Location:</span> {property.location}
                </p>

                {/* Combined Key Features */}
                <p className="location-title">
                    {keyDataLine}
                </p>
                
                {/* Amenities Section */}
                <h4>Amenities</h4>
                <ul className="amenities-list">
                    {/* Use map() to render the amenities list with simple bullet points */}
                    {property.amenities.map((amenity, index) => (
                        <li key={index}>{amenity}</li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

// ------------------------------------------
// Main Component: The Comparison App (No major changes here)
// ------------------------------------------
const PropertyComparison = () => {
    // 1. STATE: Manage the selected locations (kept default for display)
    const [location1, setLocation1] = useState('Chicago, IL'); 
    const [location2, setLocation2] = useState('Seattle, WA');
    // 2. STATE: Manage the properties to display
    const [comparisonProps, setComparisonProps] = useState({ prop1: null, prop2: null });

    const updateComparisonProperties = (loc1, loc2) => {
        const propA = propertyData.find(p => p.location === loc1) || null;
        const propB = propertyData.find(p => p.location === loc2) || null;
        setComparisonProps({ prop1: propA, prop2: propB });
    };

    React.useEffect(() => {
        updateComparisonProperties(location1, location2);
    }, []);

    const handleCompare = () => {
        if (!location1 || !location2 || location1 === location2) {
             console.error("Please select two different locations.");
             setComparisonProps({ prop1: null, prop2: null });
             return;
        }
        updateComparisonProperties(location1, location2);
    };

    const locationName1 = comparisonProps.prop1?.location || location1;
    const locationName2 = comparisonProps.prop2?.location || location2;

    return (
        <div className="property-comparison-app">
            
            <header className="app-header">
                {/* Simplified Header Title */}
                <div className="header-title">
                    Property Comparison: {locationName1} vs. {locationName2}
                </div>

                <div className="input-group">
                    {/* Dropdown 1 */}
                    <select 
                        id="location1" 
                        className="location-input"
                        value={location1}
                        onChange={(e) => setLocation1(e.target.value)}
                    >
                        {availableLocations.map(loc => (
                            <option key={loc} value={loc}>{loc}</option>
                        ))}
                    </select>

                    {/* Dropdown 2 */}
                    <select 
                        id="location2" 
                        className="location-input"
                        value={location2}
                        onChange={(e) => setLocation2(e.target.value)}
                    >
                        {availableLocations.map(loc => (
                            <option key={loc} value={loc}>{loc}</option>
                        ))}
                    </select>

                    {/* Button */}
                    <button 
                        className="compare-button" 
                        onClick={handleCompare}
                        disabled={!location1 || !location2 || location1 === location2}
                    >
                        COMPARE
                    </button>
                </div>
            </header>

            {/* Main container where cards are rendered */}
            <main id="comparison-container" className="comparison-container">
                {/* Label is now the property title, not "Property A" */}
                <PropertyCard property={comparisonProps.prop1} label="Property A" /> 
                <PropertyCard property={comparisonProps.prop2} label="Property B" />
            </main>

        </div>
    );
};

export default PropertyComparison;
