// frontend/src/PropertyComparison.js
import React, { useState } from 'react';
import { propertyData, getUniqueLocations } from './data'; 

// Pre-calculate unique locations once
const availableLocations = getUniqueLocations(propertyData);

// Icon mapping for Key Features based on the image's visual style
const featureIcons = {
    Bedrooms: 'ðŸ›',
    Bathrooms: 'ðŸ›',
    Size: 'ðŸ“',
};

// Icon mapping for Amenities based on the image's visual style
const amenityIcons = {
    'Private Dock': 'ðŸš¤',
    'Boat Parking': 'âš“',
    'BBQ Area': ' grilling',
    'Home Office': 'ðŸ’»',
    'Solar Panels': 'â˜€ï¸',
    'Two-Car Garage': 'ðŸš—',
};


// ------------------------------------------
// Helper Component: The Property Card
// ------------------------------------------
const PropertyCard = ({ property, label }) => {
    if (!property) {
        // Fallback for when no property is selected/found
        return (
            <div className="property-card empty-card">
                <h3 className="card-label">{label}</h3>
                <p>Select a location from the dropdowns above to see a comparison.</p>
            </div>
        );
    }
    
    const formattedPrice = property.price.toLocaleString('en-US', {
        style: 'currency',
        currency: 'USD',
        maximumFractionDigits: 0,
    });

    // Determine what to display for size
    const sizeDisplay = property.size_sqft.includes('/') ? 
        property.size_sqft : 
        `${property.size_sqft} sq/ft`;

    return (
        <div className="property-card">
            {/* The label is now Property A in Chicago, IL */}
            <h3 className="card-label">{label} in {property.location}</h3>
            
            <img 
                src={property.image_url} 
                alt={property.title} 
                className="property-image" 
            />
            
            <div className="card-details">
                {/* Price and Title */}
                <p className="price-tag">
                    {formattedPrice} <span className="est-tag">EST</span>
                </p>
                <p className="location-title">{property.title}</p>
                
                {/* Key Features Section */}
                <h4>Key Features</h4>
                <ul className="features-list key-features-section">
                    <li>
                        <span style={{ marginRight: '10px' }}>{featureIcons.Bedrooms}</span>
                        <strong>Bedrooms:</strong> {property.bedrooms}
                    </li>
                    <li>
                        <span style={{ marginRight: '10px' }}>{featureIcons.Bathrooms}</span>
                        <strong>Bathrooms:</strong> {property.bathrooms}
                    </li>
                    <li>
                        <span style={{ marginRight: '10px' }}>{featureIcons.Size}</span>
                        <strong>Size:</strong> {sizeDisplay}
                    </li>
                </ul>
                
                {/* Amenities Section */}
                <h4>Amenities</h4>
                <ul className="amenities-list">
                    {property.amenities.map((amenity, index) => (
                        <li key={index}>
                            <span style={{ marginRight: '10px' }}>
                                {/* Use an icon if available, otherwise fallback */}
                                {amenityIcons[amenity] || 'â˜…'} 
                            </span>
                            {amenity}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

// ------------------------------------------
// Main Component: The Comparison App
// ------------------------------------------
const PropertyComparison = () => {
    // 1. STATE: Manage the selected locations (initialized to match the image's default state)
    const [location1, setLocation1] = useState('Chicago, IL'); 
    const [location2, setLocation2] = useState('Seattle, WA');
    // 2. STATE: Manage the properties to display
    const [comparisonProps, setComparisonProps] = useState({ prop1: null, prop2: null });

    // Utility function to find and set properties based on current state
    const updateComparisonProperties = (loc1, loc2) => {
        const propA = propertyData.find(p => p.location === loc1) || null;
        const propB = propertyData.find(p => p.location === loc2) || null;
        setComparisonProps({ prop1: propA, prop2: propB });
    };

    // Initial load/mount effect to display the default properties (Chicago and Seattle)
    React.useEffect(() => {
        updateComparisonProperties(location1, location2);
    }, []); // Empty dependency array ensures this runs once on mount

    // Handle the button click
    const handleCompare = () => {
        // Validation is primarily handled by the button's disabled state, 
        // but we keep the logic here to perform the lookup
        if (!location1 || !location2 || location1 === location2) {
             console.error("Please select two different locations.");
             setComparisonProps({ prop1: null, prop2: null });
             return;
        }
        updateComparisonProperties(location1, location2);
    };

    return (
        <div className="property-comparison-app">
            
            <header className="app-header">
                {/* Removed the logo div to match the image UI */}
                <div className="input-group">
                    {/* Dropdown 1 */}
                    <select 
                        id="location1" 
                        className="location-input"
                        value={location1}
                        onChange={(e) => setLocation1(e.target.value)}
                    >
                        {/* Removed the "Select Location 1" default option */}
                        {availableLocations.map(loc => (
                            <option key={loc} value={loc}>{loc}</option>
                        ))}
                    </select>

                    {/* Dropdown 2 */}
                    <select 
                        id="location2" 
                        className="location-input"
                        value={location2}
                        onChange={(e) => setLocation2(e.target.value)}
                    >
                        {/* Removed the "Select Location 2" default option */}
                        {availableLocations.map(loc => (
                            <option key={loc} value={loc}>{loc}</option>
                        ))}
                    </select>

                    {/* Button */}
                    <button 
                        className="compare-button" 
                        onClick={handleCompare}
                        disabled={!location1 || !location2 || location1 === location2}
                    >
                        COMPARE
                    </button>
                </div>
            </header>

            {/* Main container where cards are rendered */}
            <main id="comparison-container" className="comparison-container">
                <PropertyCard property={comparisonProps.prop1} label="Property A" />
                <PropertyCard property={comparisonProps.prop2} label="Property B" />
            </main>

        </div>
    );
};

export default PropertyComparison;
