// frontend/src/PropertyComparison.js (Updated PropertyCard)

// ... (imports) ...

// Icon mapping for Key Features based on the image's visual style
const featureIcons = {
    Bedrooms: 'ðŸ›',
    Bathrooms: 'ðŸ›',
    Size: 'ðŸ“',
};

// Icon mapping for Amenities based on the image's visual style
const amenityIcons = {
    'Private Dock': 'ðŸš¤',
    'Boat Parking': 'âš“',
    'BBQ Area': 'ðŸ”¥',
    'Home Office': 'ðŸ’»',
    'Solar Panels': 'â˜€ï¸',
    'Two-Car Garage': 'ðŸš—',
};


// ------------------------------------------
// Helper Component: The Property Card
// ------------------------------------------
const PropertyCard = ({ property, label }) => {
    if (!property) {
        // ... (error fallback JSX) ...
        return (
            <div className="property-card empty-card">
                <h3 className="card-label">{label}</h3>
                <p>Select a location from the dropdowns above to see a comparison.</p>
            </div>
        );
    }
    
    const formattedPrice = property.price.toLocaleString('en-US', {
        style: 'currency',
        currency: 'USD',
        maximumFractionDigits: 0,
    });

    // FIX: Ensure size_sqft is a string before calling includes() to prevent runtime error
    const sizeString = String(property.size_sqft);
    
    // Content & Scannability Cons: Updated to clean up the unit display
    // We check for complex size (e.g., 2000/2500) and format simple size as "2000 sq. ft."
    const sizeDisplay = sizeString.includes('/') ? 
        sizeString + ' sq. ft' : 
        `${sizeString} sq. ft.`;

    // --- Helper function to render an icon and label, pushing the value to the right ---
    const renderFeatureItem = (icon, labelText, value) => (
        <li>
            <div style={{ display: 'flex', alignItems: 'center' }}>
                <span className="feature-icon" aria-label={labelText}>{icon}</span>
                {labelText}
            </div>
            {/* Content & Scannability Cons: Ensures consistent data placement (value on the far right) */}
            <strong>{value}</strong> 
        </li>
    );

    return (
        <div className="property-card">
            <h3 className="card-label">{label} in {property.location}</h3>
            
            <img 
                src={property.image_url} 
                alt={property.title} 
                className="property-image" 
            />
            
            <div className="card-details">
                {/* Price and Title */}
                <p className="price-tag">
                    {formattedPrice} 
                    {/* Confusing "EST" Tag: Added title/tooltip for clarity */}
                    <span 
                        className="est-tag"
                        title="Estimated Price" 
                    >
                        EST
                    </span>
                </p>
                <p className="location-title">{property.title}</p>
                
                {/* Key Features Section */}
                <h4>Key Features</h4>
                <ul className="features-list">
                    {renderFeatureItem(featureIcons.Bedrooms, "Bedrooms", property.bedrooms)}
                    {renderFeatureItem(featureIcons.Bathrooms, "Bathrooms", property.bathrooms)}
                    {renderFeatureItem(featureIcons.Size, "Size", sizeDisplay)}
                </ul>
                
                {/* Amenities Section */}
                <h4>Amenities</h4>
                <ul className="amenities-list">
                    {property.amenities.map((amenity, index) => (
                        <li key={index}>
                            <div style={{ display: 'flex', alignItems: 'center' }}>
                                <span className="amenity-icon" aria-label={amenity}>
                                    {amenityIcons[amenity] || 'â˜…'} 
                                </span>
                                {amenity}
                            </div>
                            {/* No value for amenities, just the label */}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

// ... (rest of the PropertyComparison component remains the same)

export default PropertyComparison;
