// frontend/src/PropertyComparison.js
import React, { useState } from 'react';
import { propertyData, getUniqueLocations } from './data'; 
import './App.css'; 

const availableLocations = getUniqueLocations(propertyData);

const PropertyCard = ({ property, label }) => {
    if (!property) {
        return (
            <div className="property-card empty-card">
                <div style={{padding: '20px'}}>
                    <h3 style={{margin: 0}}>{label}</h3>
                    <p style={{marginTop: '10px', color: '#666'}}>Select a location above.</p>
                </div>
            </div>
        );
    }
    
    const originalPrice = property.price;
    const predictedPrice = property.predicted_price;
    const formattedOriginal = typeof originalPrice === 'number' ? originalPrice.toLocaleString('en-US', { style: 'currency', currency: 'USD', maximumFractionDigits: 0 }) : null;
    const formattedPredicted = typeof predictedPrice === 'number' ? predictedPrice.toLocaleString('en-US', { style: 'currency', currency: 'USD', maximumFractionDigits: 0 }) : null;

    return (
        <div className="property-card">
            <h3 className="card-label">{property.title}</h3>
            <img 
                src={property.image_url} 
                alt={property.title} 
                className="property-image" 
            />
            
            <div className="card-details">
                
                {/* Price and Stats Wrapper */}
                <div className="price-stats-wrapper">
                    <div className="price-tag">
                        {formattedPredicted ? (
                            <div>
                                <div style={{fontSize: '0.9rem', color: '#333'}}>Predicted: <strong>{formattedPredicted}</strong></div>
                                {formattedOriginal && <div style={{fontSize: '0.8rem', color: '#666', marginTop: '4px'}}>Original: {formattedOriginal}</div>}
                            </div>
                        ) : (
                            formattedOriginal || '—'
                        )}
                    </div>
                    
                    <div className="stats-row">
                        <span className="stat-item">
                            <strong>{property.bedrooms}</strong>&nbsp;bds
                        </span>
                        <span className="stat-separator">|</span>
                        
                        <span className="stat-item">
                            <strong>{property.bathrooms}</strong>&nbsp;ba
                        </span>
                        <span className="stat-separator">|</span>
                        
                        <span className="stat-item">
                            <strong>{property.size_sqft.toLocaleString()}</strong>&nbsp;sqft
                        </span>
                    </div>
                </div>

                {/* Location */}
                <div className="address-text">
                    {property.location}
                </div>
                
                {/* Amenities Section - UPDATED */}
                {/* We use a top border to create the separator line shown in your image */}
                <div style={{borderTop: '1px solid #e0e0e0', paddingTop: '5px'}}>
                    <ul className="amenities-list">
                        {property.amenities.map((amenity, index) => (
                            <li key={index}>{amenity}</li>
                        ))}
                    </ul>
                </div>

            </div>
        </div>
    );
};

// ... (Main PropertyComparison component remains unchanged) ...
const PropertyComparison = () => {
    const [location1, setLocation1] = useState('Chicago, IL'); 
    const [location2, setLocation2] = useState('Seattle, WA');
    const [comparisonProps, setComparisonProps] = useState({ prop1: null, prop2: null });

    const updateComparisonProperties = (loc1, loc2) => {
        const propA = propertyData.find(p => p.location === loc1) || null;
        const propB = propertyData.find(p => p.location === loc2) || null;
        setComparisonProps({ prop1: propA, prop2: propB });
    };

    React.useEffect(() => {
        updateComparisonProperties(location1, location2);
    }, []);

    const handleCompare = async () => {
        if (!location1 || !location2 || location1 === location2) {
             setComparisonProps({ prop1: null, prop2: null });
             return;
        }

        // Try backend /compare first to get predicted prices
        try {
    const res = await fetch('http://localhost:8000/compare', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ address1: location1, address2: location2 }),
    });
    if (res.ok) {
        const data = await res.json();
        const a = data.result && data.result.address1 ? data.result.address1 : null;
        const b = data.result && data.result.address2 ? data.result.address2 : null;
        setComparisonProps({ prop1: a, prop2: b });
        return;
    }
} catch (err) {
    console.warn('Backend compare failed, falling back to local data', err);
}

        // Fallback: use local data only
        updateComparisonProperties(location1, location2);
    };

    return (
        <div className="property-comparison-app">
            <header className="app-header">
                {/* 1. Added the Title here */}
                <h1 className="header-title">Property Comparison</h1>

                <div className="input-group">
                    <select className="location-input" value={location1} onChange={(e) => setLocation1(e.target.value)}>
                        {availableLocations.map(loc => <option key={loc} value={loc}>{loc}</option>)}
                    </select>
                    
                    {/* 2. Added class 'vs-label' for grey styling */}
                    <span className="vs-label">vs</span>
                    
                    <select className="location-input" value={location2} onChange={(e) => setLocation2(e.target.value)}>
                        {availableLocations.map(loc => <option key={loc} value={loc}>{loc}</option>)}
                    </select>
                    
                    <button className="compare-button" onClick={handleCompare} disabled={!location1 || !location2 || location1 === location2}>
                        Compare
                    </button>
                </div>
            </header>
            
            <main className="comparison-container">
                <PropertyCard property={comparisonProps.prop1} label="Property A" /> 
                <PropertyCard property={comparisonProps.prop2} label="Property B" />
            </main>
        </div>
    );
};

export default PropertyComparison;
