// frontend/src/PropertyComparison.js
import React, { useState } from 'react';
import { propertyData, getUniqueLocations } from './data'; 

// Pre-calculate unique locations once
const availableLocations = getUniqueLocations(propertyData);

// ------------------------------------------
// Helper Component: The Property Card (JSX version of renderPropertyCard)
// ------------------------------------------
const PropertyCard = ({ property, label }) => {
    if (!property) {
        return (
            <div className="property-card empty-card">
                <h3>{label}</h3>
                <p>No property found in the selected location.</p>
            </div>
        );
    }
    
    // Format price using JS to match your static site logic
    const formattedPrice = property.price.toLocaleString('en-US', {
        style: 'currency',
        currency: 'USD',
        maximumFractionDigits: 0,
    });

    return (
        <div className="property-card">
            <h3 className="card-label">{label} in {property.location}</h3>
            <img 
                src={property.image_url} 
                alt={property.title} 
                className="property-image" 
                loading="lazy" // <<< ADD THIS ATTRIBUTE
            />
            
            <div className="card-details">
                <p className="price-tag">
                    {formattedPrice} <span className="est-tag">EST</span>
                </p>
                <p className="location-title">{property.title}</p>
                
                <h4>Key Features</h4>
                <ul className="features-list">
                    <li><strong>Bedrooms:</strong> {property.bedrooms}</li>
                    <li><strong>Bathrooms:</strong> {property.bathrooms}</li>
                    <li><strong>Size:</strong> {property.size_sqft} sq/ft</li>
                </ul>
                
                <h4>Amenities</h4>
                <ul className="amenities-list">
                    {/* Use map() to render the amenities list */}
                    {property.amenities.map((amenity, index) => (
                        <li key={index}>{amenity}</li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

// ------------------------------------------
// Main Component: The Comparison App (React State version of handleCompare)
// ------------------------------------------
const PropertyComparison = () => {
    // 1. STATE: Manage the selected locations
    const [location1, setLocation1] = useState(''); 
    const [location2, setLocation2] = useState('');
    // 2. STATE: Manage the properties to display
    const [comparisonProps, setComparisonProps] = useState({ prop1: null, prop2: null });

    // Handle the button click (the React equivalent of window.handleCompare)
    const handleCompare = () => {
        // Validation
        if (!location1 || !location2 || location1 === location2) {
             // In a real app, you'd show an error message. For simplicity, we can reset state or do nothing.
             console.error("Please select two different locations.");
             setComparisonProps({ prop1: null, prop2: null });
             return;
        }

        // Find the first property that matches each selected location
        const propA = propertyData.find(p => p.location === location1);
        const propB = propertyData.find(p => p.location === location2);

        // Update state to trigger re-render
        setComparisonProps({ prop1: propA, prop2: propB });
    };

    return (
        <div className="property-comparison-app">
            
            <header className="app-header">
                <div className="logo">üè† HOME IQ</div>
                <div className="input-group">
                    {/* Dropdown 1: Controlled by React state */}
                    <select 
                        id="location1" 
                        className="location-input"
                        value={location1}
                        onChange={(e) => setLocation1(e.target.value)}
                    >
                        <option value="">Select Location 1</option>
                        {availableLocations.map(loc => (
                            <option key={loc} value={loc}>{loc}</option>
                        ))}
                    </select>

                    {/* Dropdown 2: Controlled by React state */}
                    <select 
                        id="location2" 
                        className="location-input"
                        value={location2}
                        onChange={(e) => setLocation2(e.target.value)}
                    >
                        <option value="">Select Location 2</option>
                        {availableLocations.map(loc => (
                            <option key={loc} value={loc}>{loc}</option>
                        ))}
                    </select>

                    {/* Button: Calls the state-updating function */}
                    <button 
                        className="compare-button" 
                        onClick={handleCompare}
                        disabled={!location1 || !location2 || location1 === location2}
                    >
                        COMPARE
                    </button>
                </div>
            </header>

            {/* Main container where cards are conditionally rendered by state */}
            <main id="comparison-container" className="comparison-container">
                <PropertyCard property={comparisonProps.prop1} label="Property A" />
                <PropertyCard property={comparisonProps.prop2} label="Property B" />
            </main>

        </div>
    );
};

export default PropertyComparison;
